mapper.select:
SELECT COUNT(*)
        FROM
        fms_ord_order AS fcc
LEFT JOIN fms_payment_order fmo on Fcc.ORDER_ID = fmo.SOURCE_ID
WHERE 1=1
 AND fcc.ORDER_TYPE = #{orderType , jdbcType=VARCHAR} 

    AND (fcc.status = #{status , jdbcType=VARCHAR}


          AND (TIMESTAMPDIFF(MINUTE,NOW(),fmo.CREATION_DATE)) < fmo.PAY_LIMIT_TIME



 OR fcc.status = #{status1 , jdbcType=VARCHAR} 
 OR fcc.status = #{status2 , jdbcType=VARCHAR} 
 OR fcc.status = #{status3 , jdbcType=VARCHAR} 
 OR fcc.status = #{status4 , jdbcType=VARCHAR} )

SELECT su.USER_ID, su.USER_TYPE, su.USER_NAME, su.employee_id, he.EMPLOYEE_CODE, he.NAME EMPLOYEE_NAME FROM sys_user AS su JOIN hr_employee he ON (su.RELATED_PARTY_ID = he.EMPLOYEE_ID AND su.USER_TYPE = 'OPERATOR') WHERE 1=1
and su.USER_ID = #{userId}
and he.NAME = #{employeeName}

SELECT fccc.*
FROM fms_cmn_channel_commission_show fccc
WHERE 1=1
 AND #{reserveDate , jdbcType=DATE} BETWEEN fccc.EFFECTIVE_START_DATE AND fccc.EFFECTIVE_END_DATE 
 AND fccc.CHANNEL_ID = #{channelId , jdbcType=DECIMAL} 
 AND fccc.DEAL_PATH = #{dealPath , jdbcType=VARCHAR} 
 AND fccc.ITEM_ID = #{itemId , jdbcType=DECIMAL} 
 AND fccc.CONTRIBUTION_PERIOD = #{contributionPeriod , jdbcType=VARCHAR} 
 AND fccc.currency = #{currency , jdbcType=VARCHAR} 

    AND (fccc.PAY_METHOD = #{payMethod , jdbcType=VARCHAR} OR (#{payMethod , jdbcType=VARCHAR} = 'FJ' AND fccc.PAY_METHOD = 'AP'))

SELECT fpi.BIG_CLASS, fpi.MID_CLASS, (SELECT scv.MEANING FROM sys_code_b scb, sys_code_value_b scv WHERE scb.code_id = scv.code_id AND scb.CODE = ( SELECT fpcs.class_name1_code FROM fms_prd_class_set fpcs WHERE fpcs.set_code = 'PRODUCTION_CLASS' ) AND scv.VALUE = fpi.BIG_CLASS ) BIG_CLASS_NAME, (SELECT scv.MEANING from sys_code_b scb, sys_code_value_b scv where scb.code_id = scv.code_id and scb.code = ( select fpcs.class_name2_code from fms_prd_class_set fpcs where fpcs.set_code = 'PRODUCTION_CLASS') AND scv.VALUE = fpi.MID_CLASS ) MID_CLASS_NAME, SUM( CASE foo.ORDER_TYPE WHEN 'INSURANCE' THEN foo.YEAR_PAY_AMOUNT ELSE foo.POLICY_AMOUNT END * CASE WHEN ffer.RATE IS NULL THEN 1 ELSE ffer.RATE END) ORDER_AMOUNT FROM fms_ord_order foo JOIN fms_prd_items fpi ON fpi.ITEM_ID = foo.ITEM_ID LEFT JOIN fms_fet_exchange_rate ffer ON ( ffer.BASE_CURRENCY = 'HKD' AND ffer.FOREIGN_CURRENCY = foo.CURRENCY AND CASE foo.ORDER_TYPE WHEN 'INSURANCE' THEN foo.EFFECTIVE_DATE WHEN 'BOND' THEN foo.ISSUE_DATE WHEN 'IMMIGRANT' THEN foo.RESERVE_DATE END BETWEEN ffer.EFFECTIVE_DATE_FROM AND ffer.EFFECTIVE_DATE_TO) WHERE fpi.BIG_CLASS IN ('BX', 'ZQ', 'DC') AND CASE foo.ORDER_TYPE WHEN 'INSURANCE' THEN 'POLICY_EFFECTIVE' WHEN 'BOND' THEN 'ISSUE_SUCCESS' WHEN 'IMMIGRANT' THEN 'BUY_SUCCESS' END = foo. STATUS AND CASE foo.ORDER_TYPE WHEN 'INSURANCE' THEN foo.EFFECTIVE_DATE WHEN 'BOND' THEN foo.ISSUE_DATE WHEN 'IMMIGRANT' THEN foo.RESERVE_DATE END IS NOT NULL
AND foo.CHANNEL_ID = #{channelId}
AND foo.APPLICANT_CUSTOMER_ID = #{customerId}
GROUP BY fpi.BIG_CLASS, fpi.MID_CLASS
